INSERT INTO "public"."materials" ("id", "title", "description", "price", "file_url", "image_url", "is_premium", "subject", "created_at") VALUES ('biomolecole_cellule_scienze', 'Dalle Biomolecole alle Cellule', 'Introduzione alle biomolecole come elementi base della vita e analisi dell'organizzazione cellulare (cellule procariotiche), struttura e funzioni. Include immagini esplicative.', '3.19', 'assets/uploads/Le Biomolecole e Le Cellule.docx', 'assets/img/scienze_biomolecole_cellule.jpg', 'true', 'Scienze', '2025-05-25 23:04:35.726403+00'), ('i_verbi_italiano_linguistica', 'I Verbi nella Lingua Italiana', 'Guida completa sui verbi, i tempi e i modi per una padronanza perfetta dell'italiano, con esempi e tabelle esplicative.', '0.00', 'assets/uploads/I Verbi in Italiano.docx', 'assets/img/italiano_verbi.jpg', 'false', 'Italiano', '2025-05-25 23:04:35.726403+00'), ('la_circonferenza_matematica', 'La Circonferenza', 'Studio approfondito della circonferenza in geometria analitica: definizione, equazione canonica, condizioni di esistenza e casi particolari, con esercizi risolti. Include esempi grafici.', '3.99', 'assets/uploads/La Circonferenza.docx', 'assets/img/matematica_circonferenza.jpg', 'true', 'Matematica', '2025-05-25 23:04:35.726403+00'), ('la_costituzione_diritto', 'Struttura e Caratteri della Costituzione Italiana', 'Analisi degli articoli della Costituzione Italiana (Parte I e II, Principi Fondamentali, Disposizioni Transitorie) e delle sue caratteristiche peculiari (popolarità, rigidità, lunghezza, programmaticità).', '2.49', 'assets/uploads/La Costituzione.docx', 'assets/img/diritto_costituzione.jpg', 'true', 'Diritto', '2025-05-25 23:04:35.726403+00'), ('le_aziende_economia', 'Diversi Tipi di Aziende', 'Classificazione delle aziende (di consumo, di produzione, composte) e approfondimento sugli elementi costitutivi, l'assetto istituzionale, organizzativo, il patrimonio e i settori economici.', '2.79', 'assets/uploads/Le Aziende.docx', 'assets/img/economia_aziende.jpg', 'true', 'Economia Aziendale', '2025-05-25 23:04:35.726403+00'), ('le_forme_di_stato_diritto', 'Le Forme di Stato', 'Spiegazione delle diverse forme di stato (assoluto, liberale, socialista, totalitario, democratico) e della loro evoluzione storica, con focus sulle caratteristiche di ciascuna.', '2.00', 'assets/uploads/Le Forme di Stato.docx', 'assets/img/diritto_forme_stato.jpg', 'true', 'Diritto', '2025-05-25 23:04:35.726403+00'), ('le_proteine_scienze', 'Le Proteine', 'Studio dettagliato delle proteine: struttura (primaria, secondaria, terziaria, quaternaria), amminoacidi come monomeri, legame peptidico, denaturazione e le loro molteplici funzioni (strutturale, enzimatica, ormonale, trasporto, contrattile, immunitaria). Include schemi molecolari.', '4.25', 'assets/uploads/Le Proteine.docx', 'assets/img/scienze_le_proteine.jpg', 'true', 'Scienze', '2025-05-25 23:04:35.726403+00'), ('misure_capacita_matematica', 'Le Misure di Capacità', 'Guida alle misure di capacità, con l'unità di misura (litro) e i suoi multipli e sottomultipli. Include tabelle esplicative.', '0.00', 'assets/uploads/Le Misure di Capacità.docx', 'assets/img/matematica_misure_capacita.jpg', 'false', 'Matematica', '2025-05-25 23:04:35.726403+00'), ('misure_peso_matematica', 'Le Misure di Peso o Massa', 'Introduzione alle misure di peso o massa, con l'unità di misura (chilogrammo) e i suoi multipli e sottomultipli, inclusi tonnellata e quintale. Include tabelle esplicative.', '0.00', 'assets/uploads/Le Misure di Peso.docx', 'assets/img/matematica_misure_peso.jpg', 'false', 'Matematica', '2025-05-25 23:04:35.726403+00'), ('parabola_3_punti_matematica', 'Parabola Passante per 3 Punti', 'Spiegazione dettagliata su come scrivere l'equazione di una parabola con asse di simmetria verticale, passante per tre punti dati, con calcoli passo-passo.', '4.50', 'assets/uploads/Parabola 3 Punti.docx', 'assets/img/matematica_parabola_3_punti.jpg', 'true', 'Matematica', '2025-05-25 23:04:35.726403+00'), ('parabola_punto_vertice_matematica', 'Parabola Passante per Punto e Vertice', 'Metodologia per scrivere l'equazione di una parabola con asse di simmetria verticale, dati un punto e il vertice, con tutte le equazioni e i passaggi risolutivi.', '4.50', 'assets/uploads/Parabola Punto Vertice.docx', 'assets/img/matematica_parabola_punto_vertice.jpg', 'true', 'Matematica', '2025-05-25 23:04:35.726403+00'), ('particelle_atomi_chimica', 'Le Particelle Elementari, Atomi e Ioni', 'Approfondimento su proprietà fisiche e chimiche degli elementi, molecole, composti ionici e formulazione chimica. Include esempi e dettagli sui pedici.', '3.50', 'assets/uploads/Le Particelle e gli Atomi.docx', 'assets/img/chimica_particelle_atomi.jpg', 'true', 'Chimica', '2025-05-25 23:04:35.726403+00'), ('prima_guerra_mondiale_storia', 'La Prima Guerra Mondiale: Dalle Alleanze al Conflitto', 'Riassunto degli eventi chiave della Prima Guerra Mondiale, dalle grandi alleanze (Triplice Alleanza e Intesa) all'inizio del conflitto e i suoi cambiamenti geopolitici.', '0.00', 'assets/uploads/mappa1samueleguerrarussomondiale.pptx', 'assets/img/storia_prima_guerra_mondiale.jpg', 'false', 'Storia', '2025-05-25 23:04:35.726403+00'), ('scadenza_adeguata_economia', 'La Scadenza Adeguata', 'Spiegazione del concetto di scadenza adeguata nel contesto della gestione dei debiti e dei pagamenti, con un esempio pratico di calcolo e una tabella esplicativa.', '0.99', 'assets/uploads/La Scadenza Adeguata.docx', 'assets/img/economia_scadenza_adeguata.jpg', 'true', 'Economia Politica', '2025-05-25 23:04:35.726403+00'), ('sistema_muscolare_scienze', 'Il Sistema Muscolare', 'Esplorazione completa del sistema muscolare umano: tipologie di muscoli (scheletrici, involontari, cardiaco), struttura, funzionamento e movimenti. Include tabelle esplicative dei movimenti.', '2.99', 'assets/uploads/Il Sistema Muscolare.pdf', 'assets/img/scienze_sistema_muscolare.jpg', 'true', 'Scienze', '2025-05-25 23:04:35.726403+00'), ('strumenti_bancari_economia', 'Gli Strumenti Bancari di Regolamento', 'Approfondimento sugli strumenti di pagamento bancari: contanti, assegni, bonifici e carte di pagamento. Include dettagli su normativa e tipologie.', '0.00', 'assets/uploads/Gli Strumenti Bancari.docx', 'assets/img/economia_strumenti_bancari.jpg', 'false', 'Economia Aziendale', '2025-05-25 23:04:35.726403+00'), ('tempo_verbi_italiano_linguistica', 'Il Tempo dei Verbi in Italiano', 'Analisi dettagliata dei tempi verbali in italiano (passato prossimo, imperfetto, passato remoto, trapassato prossimo e remoto) con esempi pratici e tabelle riassuntive.', '1.49', 'assets/uploads/Il Tempo dei Verbi in Italiano.docx', 'assets/img/italiano_tempi_verbi.jpg', 'true', 'Italiano', '2025-05-25 23:04:35.726403+00'), ('unita_misura_matematica', 'Le Unità di Misura', 'Storia e evoluzione delle unità di misura, dal sistema non standard alle unità uguali per tutti (metro, chilogrammo, litro). Include dettagli su multipli e sottomultipli di lunghezza con esempi.', '0.00', 'assets/uploads/Le Unità di Misura.docx', 'assets/img/matematica_unita_misura.jpg', 'false', 'Matematica', '2025-05-25 23:04:35.726403+00');